import { Component } from '@angular/core';
import { SafeUrl } from '@angular/platform-browser';

@Component({
  selector: 'app-qr',
  templateUrl: './qr.component.html',
  styleUrls: ['./qr.component.css']
})

export class QrComponent {
  public qrdata="Please type the text you want to convert into a QR code here.";
  url:SafeUrl='';
  onCodeChange(url:SafeUrl){
    this.url=url;

  }

}
<p style="color:white">Please type the text you want to convert into a QR code </p>
<textarea type="text" class="ta"[(ngModel)]="qrdata" cols="40" rows="3" style="background-color:white;color:black;resize:none"></textarea>

<br>
<br>
<qrcode (qrCodeURL)="onCodeChange($event)" width:1000 [qrdata]="qrdata" ></qrcode>
<br> <br>
<a [href]="url" download class="bn5">Download QR Image</a>
